file = ""


# find index of first 4 unique characters in a row
def find_index_start(string):
    for i in range(len(string) - 3):
        if len(set(string[i:i+4])) == 4:
            return i
    
    return -1

print(find_index_start(file) +14)

def find_index_message(string):
    for i in range(len(string) - 13):
        if len(set(string[i:i+14])) == 14:
            return i
    
    return -1

print(find_index_message(file) +14)
